#!/bin/bash

echo '----------------------------------------------------------------------------------------------------------------------------------------------------------------'
echo "[+] Status Collector is runnin'...">> $RECON/$1/log.txt
echo '----------------------------------------------------------------------------------------------------------------------------------------------------------------'
for i in $(cat $RECON/$1/probed/httprobe);do curl -I $i -s | grep "HTTP" | cut -d " " -f 2 > $RECON/$1/probed/tmp.txt; for j in $(cat $RECON/$1/probed/tmp.txt);do echo $j" : "$i >> $RECON/$1/probed/status-https; done; done
echo '----------------------------------------------------------------------------------------------------------------------------------------------------------------'
echo "[+] Boss! I have collected status of probed sites for you <3"
echo '----------------------------------------------------------------------------------------------------------------------------------------------------------------'